import { createStore } from "vuex";

const store = createStore({
  state: {
    TopStoryID: null,
  },
  mutations: {
    setTopStoryID(state, data) {
      state.TopStoryID = data;
    },
  },
  getters: {
    getTopStoryID(state) {
      return state.TopStoryID;
    },
  },
});

export default store;
